import com.elements.LabelElement;
import org.testng.Assert;
import org.testng.annotations.DataProvider;
import org.testng.annotations.Test;
import com.pageObject.EcoNewsPO;

public class CreateNewsContentFieldTest extends BasicTest{
    @DataProvider()
    public Object[][] inCorrectDataEmpty(){
        return new Object[][]{
                {"",
                        "#ff0000"}
        };
    }

    @DataProvider()
    public Object[][] inCorrectDataLessThenT20(){
        return new Object[][]{
                {"abcdefghig",
                        "#ff0000"}
        };
    }
    @DataProvider()
    public Object[][] correctDataForText(){
        return new Object[][]{
                {"This is correct message with more than 20 symbols.",
                        "#878787"}
        };
    }
    @DataProvider()
    public Object[][] inCorrectDataLessMoreThen63207(){
        return new Object[][]{
                {"testestestestestestestestestestestestestestestestestestestestestestestest"  + //63208 charecters
                        "",
                        "#ff0000"}
        };
    }
    @Test(dataProvider = "inCorrectDataEmpty")
    public void verifyTextFieldinEmpty(String mainText, String ExpectedText) {
        LabelElement contentFieldMessage = new EcoNewsPO(webDriver)
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentMassageLabel();
        Assert.assertEquals(contentFieldMessage.getColorHex(), ExpectedText);
    }

    @Test(dataProvider = "inCorrectDataLessThenT20")
    public void verifyTextFieldLessThan20(String mainText, String ExpectedText) {
        LabelElement contentFieldMessage = new EcoNewsPO(webDriver)
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentMassageLabel();
        Assert.assertEquals(contentFieldMessage.getColorHex(), ExpectedText);
    }

    @Test(dataProvider = "correctDataForText")
    public void verifyTextFieldCorrect(String mainText, String ExpectedText) {
        LabelElement contentFieldMessage = new EcoNewsPO(webDriver)
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentMassageLabel();
        Assert.assertEquals(contentFieldMessage.getColorHex(), ExpectedText);
    }

    @Test(dataProvider = "inCorrectDataLessMoreThen63207")
    public void verifyTextFieldMoreThen63207(String mainText, String ExpectedText) {
        LabelElement contentFieldMessage = new EcoNewsPO(webDriver)
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentMassageLabel();
        Assert.assertEquals(contentFieldMessage.getColorHex(), ExpectedText);
    }
}
