import com.elements.LabelElement;
import com.pageObject.CreateNewsPO;
import org.openqa.selenium.By;
import org.openqa.selenium.WebDriver;
import org.openqa.selenium.chrome.ChromeDriver;
import org.testng.Assert;
import org.testng.annotations.AfterClass;
import org.testng.annotations.BeforeClass;
import org.testng.annotations.DataProvider;
import org.testng.annotations.Test;
import com.pageObject.EcoNewsPO;
import com.pageObject.LogInPO;

public class CreateNewsContentFieldTest extends BasicTest{
    @DataProvider()
    public Object[][] inCorrectDataEmpty(){
        return new Object[][]{
                {"",
                        "#ff0000"}
        };
    }

    @DataProvider()
    public Object[][] inCorrectDataLessThenT20(){
        return new Object[][]{
                {"abcdefghig",
                        "#ff0000"}
        };
    }
    @DataProvider()
    public Object[][] correctDataForText(){
        return new Object[][]{
                {"This is correct message with more than 20 symbols.",
                        "#878787"}
        };
    }
    @DataProvider()
    public Object[][] inCorrectDataLessMoreThen63207(){
        return new Object[][]{
                {"testestestestestestestestestestestestestestestestestestestestestestestest"  + //63208 charecters
                        "",
                        "#ff0000"}
        };
    }
    @DataProvider()
    public Object[][] isAutoResizeable448px(){
        return new Object[][]{
                {"",
                        448}
        };
    }
    @Test(dataProvider = "inCorrectDataEmpty")
    public void verifyTextFieldinEmpty(String mainText, String ExpectedText) {
        LabelElement contentFieldMessage = new EcoNewsPO(webDriver)
                .getHeaderComponent()
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentMassegeLabel();
        Assert.assertEquals(contentFieldMessage.getColorHex(), ExpectedText);
    }

    @Test(dataProvider = "inCorrectDataLessThenT20")
    public void verifyTextFieldLessThan20(String mainText, String ExpectedText) {
        LabelElement contentFieldMessage = new EcoNewsPO(webDriver)
                .getHeaderComponent()
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentMassegeLabel();
        Assert.assertEquals(contentFieldMessage.getColorHex(), ExpectedText);
    }

    @Test(dataProvider = "correctDataForText")
    public void verifyTextFieldCorrect(String mainText, String ExpectedText) {
        LabelElement contentFieldMessage = new EcoNewsPO(webDriver)
                .getHeaderComponent()
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentMassegeLabel();
        Assert.assertEquals(contentFieldMessage.getColorHex(), ExpectedText);
    }

    @Test(dataProvider = "inCorrectDataLessMoreThen63207")
    public void verifyTextFieldMoreThen63207(String mainText, String ExpectedText) {
        LabelElement contentFieldMessage = new EcoNewsPO(webDriver)
                .getHeaderComponent()
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentMassegeLabel();
        Assert.assertEquals(contentFieldMessage.getColorHex(), ExpectedText);
    }
    @Test(dataProvider = "isAutoResizeable448px")
    public void isContentFieldAutoResizable(String mainText, int ExpectedHeight) {
        LabelElement contentField = new EcoNewsPO(webDriver)
                .getHeaderComponent()
                .clickEcoNews()
                .clickCreateNewsBtn()
                .setContent(mainText)
                .clickOnBlankArea()
                .getContentField();
        Assert.assertEquals(contentField.getHeight(), ExpectedHeight);
    }
}
